    return (


        <div className="FormularioCadastro_div">
            <form
            >
                <TextField
                    // value={nome}
                    // onChange={(evento) => {
                    //     let tempNome = evento.target.value
                    //     if (tempNome.length >= 3) {
                    //         tempNome = tempNome.substr(0, 3)
                    //     }
                    //     setNome(tempNome)

                    onChange={(evento) => {
                        setNome(evento.target.value)
                    }}
                    id="nome"
                    label="Nome"
                    variant="outlined"
                    className="FormularioCadastro_inputs"
                    margin="dense"
                    fullWidth


                />
                <TextField
                    onChange={(evento) => {
                        setSobrenome(evento.target.value)
                    }}
                    id="Sobrenome"
                    label="Sobrenome"
                    variant="outlined"
                    margin="dense"
                    fullWidth />
                <TextField
                    onChange={(evento) => {
                        setCpf(evento.target.value)

                    }}
                    // onBlur é um onChange mas quando vc sai do campo de preenchimento e vai para o outro, assim ele aciona
                    onBlur={(evento) => {
                        const ehvalido = props.validarCPF(cpf) //vai receber um obj com dados da validação (cpf: valido true ou false e texto)
                        setErros({ cpf: ehvalido }) // atualiza o obj erros no ampo cpf
                    }}
                    id="cpf"
                    label="CPF"
                    variant="outlined"
                    margin="dense"
                    fullWidth
                    error={!erros.cpf.valido}
                    // true fica vermelha e false normal
                    helperText={erros.cpf.texto}
                />

                <FormControlLabel
                    label="Promoções"
                    checked={promocoes}
                    control={<Switch onChange={(evento) => {
                        setPromocoes(evento.target.checked)
                    }}
                        name="Promoções" color="primary" />} />
                <FormControlLabel
                    label="Novidades"
                    checked={novidades}
                    control={<Switch onChange={(evento) => {
                        setNovidades(evento.target.checked)
                    }}
                        name="Novidades" color="primary" />} />

                <Button
                    type="submit"
                    variant="contained"
                    color="primary">Cadastrar</Button>


            </form>
        </div>


    )
}

export default FormularioJob
