    //     return acumula

    // }

    Filtra()

    return (
        <>
            <MenuLateral />
            <div className="listaAcionamentos_div">

                <table>
                    <tr>
                        {firstLine.map((recebe) => {
                            return (
                                <th>{recebe}</th>
                            )

                        })}

                        {/* <th>First Name</th>
                        <th>Last Name</th>
                        <th>Points</th> */}
                    </tr>
                    <tr>

                        {lista.map((recebe) => {
                            <td>{recebe}</td>

                        })}


                    </tr>
                </table>

                {/* <button onClick={() => { Head() }} >CLIQUE</button> */}

            </div>


        </>




    )
}

export default ListaAcionamento
